function allloader()function redstoneloader()redstone=component.proxy(component.list("redstone")())end;function inventoryloader()inventory=component.proxy(component.list("inventory_controller")())end;function experienceloader()experience=component.proxy(component.list("experience")())end;function chunkloaderloader()chunkloader=component.proxy(component.list("chunkloader")())end;function chatloader()chat=component.proxy(component.list("chat",true)())end;function robotloader()robot=component.proxy(component.list("robot")())end;function internetloader()internet=component.proxy(component.list("internet")())end;function getandsetname()if robot==nil then name="generic"else name=robot.name()end;chat.setName(name)end;function loop()controluser=""out=""while true do local a,a,b,c=computer.pullSignal("chat_message")if c=="!help"then chat.say("§2!robot §4[control, release, override, reset] §2"..name)end;if c=="!robot reset"then if b=="Myros27"then controluser=""end end;if controluser==""then if c=="!robot control "..name then controluser=b;chat.say("§2"..controluser.." took control over "..name)while controluser~=""do value="No return value"local a,a,b,c=computer.pullSignal("chat_message")if c=="!robot reset"then if b=="Myros27"then controluser=""end end;if c=="!robot override "..name then if b=="Myros27"then controluser="Myros27"chat.say("§2"..controluser.." took control over "..name)end end;if c=="!robot release "..name then if b==controluser then controluser=""chat.say("§2 released")end else if b==controluser then local d,e=load(c)if d then local f,value=pcall(d)if out~=""then if type(out)=="string"then chat.say(out)out=""end;if type(out)=="nil"then chat.say("out is nil")out=""end;if type(out)=="number"then out=tostring(out)chat.say(out)out=""end;if type(out)=="boolean"then out=tostring(out)chat.say(out)out=""end;if type(out)=="table"then for g,h in pairs(out)do chat.say(g,h)end;out=""end;if type(out)=="function"then out=tostring(out)chat.say(out)out=""end;if type(out)=="userdata"then out=tostring(out)chat.say(out)out=""end;if type(out)=="thread"then out=tostring(out)chat.say(out)out=""end end;if f then chat.say("§2executed successfully")else value=tostring(value)chat.say("§2Execution error:§7 "..value)end else e=tostring(e)chat.say("§4Compilation error:§7 "..e)end end end end end end end end;name="generic"pcall(redstoneloader)pcall(inventoryloader)pcall(experienceloader)pcall(chunkloaderloader)pcall(chatloader)pcall(robotloader)pcall(getandsetname)pcall(internetloader)while true do pcall(loop())chat.say("§2FATAL ERROR, RESET!")end end
